<?php	if ( ! defined( 'ABSPATH' ) ) {		die;	} // Cannot access pages directly.	if ( ! class_exists( 'TTBM_Status' ) ) {		class TTBM_Status {			public function __construct() {				add_action( 'admin_menu', array( $this, 'status_menu' ) );			}			public function status_menu() {				add_submenu_page( 'edit.php?post_type=ttbm_tour', __( 'Status', 'tour-booking-manager' ), __( '<span style="color:yellow">Status</span>', 'tour-booking-manager' ), 'manage_options', 'ttbm_status_page', array( $this, 'ttbm_status_page' ) );			}			public function ttbm_status_page() {				$wp_v       = get_bloginfo( 'version' );				$wc_v       = WC()->version;				$wc_i       = TTBM_Woocommerce_Plugin::check_woocommerce();;				$from_name  = get_option( 'woocommerce_email_from_name' );				$from_email = get_option( 'woocommerce_email_from_address' );				?>				<!-- Create a header in the default WordPress 'wrap' container -->				<div class="wrap">					<?php do_action( 'ttbm_status_notice_sec' ); ?>					<table class="wc_status_table widefat" cellspacing="0" id="status">						<thead>						<tr>							<th colspan="3" data-export-label="WordPress Environment"><h2>Tour Booking Manager For Woocommerce Environment Status</h2></th>						</tr>						</thead>						<tbody>						<tr>							<td data-export-label="WC Version">WordPress Version:</td>							<td class="help"><span class="woocommerce-help-tip"></span></td>							<td>								<?php if ( $wp_v > 5.5 ) {									echo '<span class="ttbm_success"> <span class="dashicons dashicons-saved"></span>' . $wp_v . '</span>';								} else {									echo '<span class="ttbm_warning"> <span class="dashicons dashicons-saved"></span>' . $wp_v . '</span>';								} ?>							</td>						</tr>						<tr>							<td data-export-label="WC Version">Woocommerce Installed:</td>							<td class="help"><span class="woocommerce-help-tip"></span></td>							<td>								<?php if ( $wc_i ==1) {									echo '<span class="ttbm_success"> <span class="dashicons dashicons-saved"></span>Yes</span>';								} else {									echo '<span class="ttbm_error"> <span class="dashicons dashicons-no-alt"></span>No</span>';								} ?>							</td>						</tr>						<?php if ( $wc_i ==1) { ?>							<tr>								<td data-export-label="WC Version">Woocommerce Version:</td>								<td class="help"><span class="woocommerce-help-tip"></span></td>								<td><?php if ( $wc_v > 4.8 ) {										echo '<span class="ttbm_success"> <span class="dashicons dashicons-saved"></span>' . $wc_v . '</span>';									} else {										echo '<span class="ttbm_warning"> <span class="dashicons dashicons-no-alt"></span>' . $wc_v . '</span>';									} ?>								</td>							</tr>							<tr>								<td data-export-label="WC Version">Email From Name:</td>								<td class="help"><span class="woocommerce-help-tip"></span></td>								<td>									<?php if ( $from_name ) {										echo '<span class="ttbm_success"> <span class="dashicons dashicons-saved"></span>' . $from_name . '</span>';									} else {										echo '<span class="ttbm_error"> <span class="dashicons dashicons-no-alt"></span></span>';									} ?>								</td>							</tr>							<tr>								<td data-export-label="WC Version">From Email Address:</td>								<td class="help"><span class="woocommerce-help-tip"></span></td>								<td>									<?php if ( $from_email ) {										echo '<span class="ttbm_success"> <span class="dashicons dashicons-saved"></span>' . $from_email . '</span>';									} else {										echo '<span class="ttbm_error"> <span class="dashicons dashicons-no-alt"></span></span>';									} ?>								</td>							</tr>						<?php }							do_action( 'ttbm_status_table_item_sec' ); ?>						</tbody>					</table>				</div>				<?php			}		}		new TTBM_Status();	}